    @Override
    public void submitLoginByEmailPassword(String email, String password) {

        /*
        if (firebaseAuth.getCurrentUser() != null) {
            getUserData();
        }
       */
        view.showProgressBar("");
        firebaseAuth.signInWithEmailAndPassword(email, password).addOnCompleteListener((Activity) context, new OnCompleteListener<AuthResult>() {
            @Override
            public void onComplete(@NonNull Task<AuthResult> task) {
                if (task.isSuccessful()) {
                    Log.d("sukses","sukses");
                    getUserData("FIREBASE");
                } else if (!task.isSuccessful()) {
                    Log.d("failed","failed");
                    view.submitResult(false);
                }
                view.hideProgressBar();
            }
        });

    }


    @Override
    public void submitLoginByEmail(final GoogleSignInAccount acct) {
        Log.d("auth", "firebaseAuthWithGoogle:" + acct.getId());
        view.showProgressBar("Sedang login..");
        AuthCredential credential = GoogleAuthProvider.getCredential(acct.getIdToken(), null);
        firebaseAuth.signInWithCredential(credential)
                .addOnCompleteListener((Activity) context, new OnCompleteListener<AuthResult>() {
                    @Override
                    public void onComplete(@NonNull Task<AuthResult> task) {
                        Log.d("sign google", "signInWithCredential:onComplete:" + task.isSuccessful());
                        if (!task.isSuccessful()) {
                            Log.w("Task", "signInWithCredential", task.getException());
                            Log.d("failed","failed");
                            view.submitResult(false);
                        } else {
                           view.registrationByGmail(acct,task.getResult().getUser().getUid(),true);
                            getUserData("GMAIL");
                        }
                        view.hideProgressBar();
                    }
                });
    }

    @Override
    public void submitLoginByFacebook(final User user, final AccessToken token) {
        AuthCredential credential = FacebookAuthProvider.getCredential(token.getToken());
        firebaseAuth.signInWithCredential(credential)
                .addOnCompleteListener((Activity)context, new OnCompleteListener<AuthResult>() {
                    @Override
                    public void onComplete(@NonNull Task<AuthResult> task) {
                        Log.d("LoginFB", "signInWithCredential:onComplete:" + task.isSuccessful());
                        if (!task.isSuccessful()) {
                            Log.w("Task", "signInWithCredential", task.getException());
                            Log.d("failed","failed");
                            view.submitResult(false);
                        } else {
                           view.registrationByFacebook(user,task.getResult().getUser().getUid(),true);
                            getUserData("FACEBOOK");
                        }
                        view.hideProgressBar();
                    }
                });
    }

    @Override
    public void submitUserDetail(final User user) {
        DatabaseReference childRef = dbRef.child(user.getId());
        childRef.setValue(user);
        childRef.addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
               Log.d("data snapshot : ",dataSnapshot.toString());
            }
            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });
    }

    @Override
    public boolean checkEmailExist(final String email) {

        Query userQuery = dbRef.orderByChild("email").equalTo(email);
        userQuery.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Log.d("data snapshot email: ", dataSnapshot.toString());
                for(DataSnapshot singleSnapshot : dataSnapshot.getChildren()){
                    user = singleSnapshot.getValue(User.class);
                }
                if (user==null){
                    exist=true;
                    result="hasil kosong";
                    Log.d("result",result);
                }else{
                    exist=false;
                }

            }
            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });
       Log.d("exist : ",String.valueOf(exist));

        return exist;

    }

    public void getUserData(final String loginType) {
        final String userId = firebaseAuth.getCurrentUser().getUid();
        Log.d("usrId",userId);
        Query userQuery = dbRef.orderByChild("id").equalTo(userId);
        userQuery.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Log.d("data snapshot : ", dataSnapshot.toString());
                User user=null;
                for(DataSnapshot singleSnapshot : dataSnapshot.getChildren()){
                    user = singleSnapshot.getValue(User.class);
                }
                Log.d("userid response",user.getId());
               view.toDashboardPage(user,loginType);
            }
            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });
    }
    public void getUserDataByEmail(final String email) {
        Query userQuery = dbRef.orderByChild("email").equalTo(email);
        userQuery.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Log.d("data snapshot : ", dataSnapshot.toString());
                User user=null;
                for(DataSnapshot singleSnapshot : dataSnapshot.getChildren()){
                    user = singleSnapshot.getValue(User.class);
                }
                    view.toDashboardPage(user,"");
            }
            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });
    }
