    @Override
    public void submitRegistration(String loginType,String userId,String name,String gender,String bornDate,String email, String password,String province, String city,String address,String phone,String usertType ) {
        if (loginType.equals("FACEBOOK")||loginType.equals("GMAIL"))
        {
            view.showProgressBar("Update Profile..");
            view.registrationResult(userId,true);
        }else {
            view.showProgressBar("sedang mendaftar..");
            firebaseAuth.createUserWithEmailAndPassword(email, password)
                    .addOnCompleteListener((Activity) context, new OnCompleteListener<AuthResult>() {
                        @Override
                        public void onComplete(@NonNull Task<AuthResult> task) {
                            if (!task.isSuccessful()) {
                                view.registrationResult("Failed", false);
                            } else {
                                view.registrationResult(task.getResult().getUser().getUid(), true);
                            }
                        }
                    });
        }

    }


    @Override
    public void submitUserDetail(User user) {
        Log.d("userId update : ",user.getId());
        DatabaseReference childRef = dbRef.child(user.getId());
        childRef.setValue(user);
        childRef.addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                view.detailRegistrationResult(true);
            }
            @Override
            public void onCancelled(DatabaseError databaseError) {
                view.detailRegistrationResult(false);
            }
        });
    }
